import React, { useState } from 'react';
import { generate } from 'password-hash';

const RegisterPage = () => {
  const [account, setAccount] = useState({ firstName: '', surname: '', email: '', password: '', confirmPassword: '',
                                         location: '', education: '', workExperience: '', skills: ''});



  const register = async () => {
    if (account.password === account.confirmPassword)
    {
      var body = account;

      const dateAdded = new Date().toLocaleString();
      body.dateAdded = dateAdded;

      body.hashPassword = generate(account.password);
      delete body.password;
      delete body.confirmPassword;
      console.log(body);

      const result = await fetch('/api/seekers/add-new', {
        method: 'post',
        body: JSON.stringify(body),
        headers: {
          'Content-Type': 'application/json',
        }
      });
      console.log(result);
    } else {
      setAccount({ firstName: account.firstName, surname: account.surname, email: account.email, password: '',
                   confirmPassword: '', location: account.location, education: account.education,
                   workExperience: account.workExperience, skills: account.skills });
    }
};


    return (
      <>
        <div>
          <h1>Register</h1>
          <label>
            First Name:
            <input type="text" value={account.firstName} onChange={(event) => setAccount({ firstName: event.target.value, surname: account.surname, email: account.email, password: account.password,
                         confirmPassword: account.confirmPassword, location: account.location, education: account.education,
                         workExperience: account.workExperience, skills: account.skills })} />
          </label>
          <label>
            Surname:
            <input type="text" value={account.surname} onChange={(event) => setAccount({ firstName: account.firstName, surname: event.target.value, email: account.email, password: account.password,
                         confirmPassword: account.confirmPassword, location: account.location, education: account.education,
                         workExperience: account.workExperience, skills: account.skills })} />
          </label>
          <label>
            E-mail:
            <input type="text" value={account.email} onChange={(event) => setAccount({ firstName: account.firstName, surname: account.surname, email: event.target.value, password: account.password,
                         confirmPassword: account.confirmPassword, location: account.location, education: account.education,
                         workExperience: account.workExperience, skills: account.skills })} />
          </label>
          <label>
            Password:
            <input type="password" value={account.password} onChange={(event) => setAccount({ firstName: account.firstName, surname: account.surname, email: account.email, password: event.target.value,
                         confirmPassword: account.confirmPassword, location: account.location, education: account.education,
                         workExperience: account.workExperience, skills: account.skills })} />
          </label>
          <label>
            Confirm Password:
            <input type="password" value={account.confirmPassword} onChange={(event) => setAccount({ firstName: account.firstName, surname: account.surname, email: account.email, password: account.password,
                         confirmPassword: event.target.value, location: account.location, education: account.education,
                         workExperience: account.workExperience, skills: account.skills })} />
          </label>
          <label>
            Location:
            <input type="text" value={account.location} onChange={(event) => setAccount({ firstName: account.firstName, surname: account.surname, email: account.email, password: account.password,
                         confirmPassword: account.confirmPassword, location: event.target.value, education: account.education,
                         workExperience: account.workExperience, skills: account.skills })} />
          </label>
          <label>
            Education:
            <textarea rows="4" cols="50" value={account.education} onChange={(event) => setAccount({ firstName: account.firstName, surname: account.surname, email: account.email, password: account.password,
                         confirmPassword: account.confirmPassword, location: account.location, education: event.target.value,
                         workExperience: account.workExperience, skills: account.skills })} />
          </label>
          <label>
            Work Experience:
            <textarea rows="4" cols="50" value={account.workExperience} onChange={(event) => setAccount({ firstName: account.firstName, surname: account.surname, email: account.email, password: account.password,
                         confirmPassword: account.confirmPassword, location: account.location, education: account.education,
                         workExperience: event.target.value, skills: account.skills })} />
          </label>
          <label>
            Skills:
            <textarea rows="4" cols="50" value={account.skills} onChange={(event) => setAccount({ firstName: account.firstName, surname: account.surname, email: account.email, password: account.password,
                         confirmPassword: account.confirmPassword, location: account.location, education: account.education,
                         workExperience: account.workExperience, skills: event.target.value })}/>
          </label>
          <button onClick={() => register()}>Register</button>
        </div>
      </>
    );

};

export default RegisterPage;
