import React, { useState, useEffect } from 'react';


const ProfileInfoComponent = () => {
  const [profileInfo, setProfileInfo] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      const email = JSON.parse(sessionStorage.getItem('state')).account;
      const result = await fetch(`/api/seekers/${email}`);
      const body = await result.json();
      setProfileInfo(body);
    }
    fetchData();
  });

  return(
    <>
      <h1> My account </h1>
      <div>
        <ul>
          <li>First Name: {profileInfo.firstName}</li>
          <li>Surname: {profileInfo.surname}</li>
          <li>E-mail: {profileInfo.email}</li>
          <li>Location: {profileInfo.location}</li>
          <li>Education: {profileInfo.education}</li>
          <li>Work Experience: {profileInfo.workExperience}</li>
          <li>Skills: {profileInfo.skills}</li>
        </ul>
      </div>
    </>
  );

};

export default ProfileInfoComponent;
