import React, { useState } from 'react';
import JobSearchList from '../pages/JobSearchList';


const SearchComponent = () => {
  const [query, setQuery] = useState('');

  const search = () => {
    window.location.replace(`vacancies/${query}`);
  };





  return(
    <>
      <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css" />
      <button type="submit" onClick={() => search()}><i class="fa fa-search"></i></button>
      <input type="search" placeholder="Search.." value={query} onChange={(event) => setQuery(event.target.value)} />
    </>
  );
};

export default SearchComponent;
