import React, { useState } from 'react';
import { verify } from 'password-hash';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const login = async () => {
    const result = await fetch(`api/seekers/${email}`);
    const accountInfo = await result.json();
    const hashPassword = accountInfo.hashPassword;
    if (verify(password, hashPassword)) {
      document.getElementById("loginSuccessful").style.display("block");

    }

  }







  return(
    <>
      <div>
        <h1>Login</h1>
        <label>
          E-mail:
          <input type="text" value={email} onChange={(event) => setEmail(event.target.value)} />
        </label>
        <label>
          Password:
          <input type="password" value={password} onChange={(event) => setPassword(event.target.value)} />
        </label>
        <p id = "loginSuccessful" class="hidden"> Login successful </p>
        <p id = "incorrectEmail" class="hidden"> This account does not exist </p>
        <p id = "incorrectPassword" class="hidden"> Incorrect password </p>
        <button onClick={() => login()}>Log in</button>
      </div>
    </>
  );




}


export default LoginPage;
