import React from 'react';


const ApplyButton = ({ jobTitle, user }) => {

  const apply = async () => {
    if (user.email) {
      const result = await fetch(`/api/submit/${user.email}/${jobTitle}`);
    } else {
      window.location.replace('/login/');
    }
  }



  return(
    <button onClick={() => apply()} >Apply</button>
  );
};

export default ApplyButton;
