import React, { useState } from 'react';

const CreateNewVacancyPage = () => {
  const [jobTitle, setJobTitle] = useState('');
  const [companyName, setCompanyName] = useState('');
  const [salary, setSalary] = useState('');
  const [location, setLocation] = useState('');
  const [essentialRequirements, setEssentialRequirements] = useState('');
  const [beneficialRequirements, setBeneficialRequirements] = useState('');
  const [description, setDescription] = useState('');

  const [jobInfo, setJobInfo] = useState({ jobTitle: '' });




  const addVacancy = async () => {
    const today = new Date();
    const dateAdded = today.getDate()+'.'+(today.getMonth()+1)+'.'+today.getFullYear()
                       + ' ' + today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
    const result = await fetch('/api/vacancies/add-new', {
      method: 'post',
      body: JSON.stringify({jobTitle, companyName, salary, location, essentialRequirements,
        beneficialRequirements, description, dateAdded}),
      headers: {
        'Content-Type': 'application/json',
      }
    });
  }



  return (
    <>
      <div>
        <h1>Add new Vacancy</h1>
        <label>
          Job Title:
          <input type="text" value={jobTitle} onChange={(event) => setJobTitle(event.target.value)} />
        </label>
        <label>
          Company Name:
          <input type="text" value={companyName} onChange={(event) => setCompanyName(event.target.value)} />
        </label>
        <label>
          Salary:
          <input type="text" value={salary} onChange={(event) => setSalary(event.target.value)} />
        </label>
        <label>
          Location:
          <input type="text" value={location} onChange={(event) => setLocation(event.target.value)} />
        </label>
        <label>
          Essential Requirements
          <textarea rows="4" cols="50" value={essentialRequirements} onChange={(event) => setEssentialRequirements(event.target.value)} />
        </label>
        <label>
          Beneficial Requirements:
          <textarea rows="4" cols="50" value={beneficialRequirements} onChange={(event) => setBeneficialRequirements(event.target.value)}/>
        </label>
        <label>
          Description:
          <textarea rows="6" cols="50" value={description} onChange={(event) => setDescription(event.target.value)} />
        </label>
        <button onClick={() => addVacancy()}>Add new vacancy</button>
      </div>



    </>




  );
}

export default CreateNewVacancyPage;
