import React, { useState } from 'react';
import { generate } from 'password-hash';

const RegisterPage = () => {
  const [account, setAccount] = useState({ firstName: '', surname: '', email: '', password: '', confirmPassword: '',
                                         location: '', education: '', workExperience: '', skills: '', salary: '', jobType: 'Permanent' });



  const register = async () => {
    if (account.password === account.confirmPassword)
    {
      var body = account;

      const dateAdded = new Date().toLocaleString();
      body.dateAdded = dateAdded;

      body.hashPassword = generate(account.password);
      delete body.password;
      delete body.confirmPassword;

      const result = await fetch('/api/seekers/add-new', {
        method: 'post',
        body: JSON.stringify(body),
        headers: {
          'Content-Type': 'application/json',
        }
      });
    } else {
      setAccount({ firstName: account.firstName, surname: account.surname, email: account.email, password: '',
                   confirmPassword: '', location: account.location, education: account.education,
                   workExperience: account.workExperience, skills: account.skills });
    }

  };

  const handleChange = (event) => {
    const accountCopy = JSON.parse(JSON.stringify(account));
    accountCopy[event.target.name] = event.target.value;
    setAccount(accountCopy);
  }



    return (
      <>
        <h1>Register</h1>
        <div>
          <div>
            <label>
              First Name:
              <input type="text" name="firstName" value={account.firstName} onChange={handleChange} />
            </label>
          </div>
          <div>
            <label>
              Surname:
              <input type="text" name="surname" value={account.surname} onChange={handleChange} />
            </label>
          </div>
          <div>
            <label>
              E-mail:
              <input type="text" name="email" value={account.email} onChange={handleChange} />
            </label>
          </div>
          <div>
            <label>
              Password:
              <input type="password" name="password" value={account.password} onChange={handleChange} />
            </label>
          </div>
          <div>
            <label>
              Confirm Password:
              <input type="password" name="confirmPassword" value={account.confirmPassword} onChange={handleChange} />
            </label>
          </div>
          <div>
            <label>
              Location:
              <input type="text" name="location" value={account.location} onChange={handleChange} />
            </label>
          </div>
          <div>
            <label>
              Education:
              <textarea rows="4" cols="50" name="education" value={account.education} onChange={handleChange} />
            </label>
          </div>
          <div>
            <label>
              Work Experience:
              <textarea rows="4" cols="50" name="workExperience" value={account.workExperience} onChange={handleChange} />
            </label>
          </div>
          <div>
            <label>
              Skills:
              <textarea rows="4" cols="50" name="skills" value={account.skills} onChange={handleChange}/>
            </label>
          </div>
          <div>
            <label>
              Minimum Expected Salary:
              <input type="number" name="salary" value={account.salary} onChange={handleChange} />
            </label>
          </div>
          <div>
            <label>
              Employment Type:
              <select name="jobType" value={account.jobType} onChange={handleChange}>
                <option value="Permanent">Permanent</option>
                <option value="Contract">Contract</option>
                <option value="Any">Any</option>
              </select>
            </label>
          </div>
        </div>
        <button onClick={() => register()}>Register</button>
      </>
    );

};

export default RegisterPage;
