import React from 'react';

const JobFindingButton = ({ user }) => {

  const typeFilter = (jobs) => {
    if (user.jobType === "Both") {
      return jobs;
    } else {
      const result = jobs.filter((job) => {
        return (user.jobType === job.jobType);
      });
      return result;
    }
  }

  const salaryFilter = (jobs) => {
    const result = jobs.filter((job) => {
      return (user.salary <= job.maximumSalary);
    });
    return result;
  }

  const educationQuality = (education) => {
    if (education === 'None') {
      return 0;
    } else if (education === 'High School Diploma') {
      return 1;
    } else if (education === "Bachelor's Degree") {
      return 2;
    } else if (education === "Master's Degree") {
      return 3;
    } else if (education === 'Doctoral Degree') {
      return 4;
    } else {
      return 0;
    }
  }

  const educationFilter = (jobs) => {
    const result = jobs.filter((job) => {
      return (educationQuality(job.education) >= educationQuality(user.education));
    });
    return result;
  }

  const stringCompare = (string1, string2) => {
    let matches = 0;
    for (let i = 0; i < string1.length; i++) {
      for (let j = 0; j < string2.length; j++) {
        if (string1[i].replace(',', '').replace('.', '').toLowerCase() === string2[j].replace(',', '').replace('.', '').toLowerCase()) {
          matches++;
        }
      }
    }
    if (string1.length > 0) {
      return matches/string1.length;
    } else {
      return 1;
    }
  }

  const checkMatching = (job, attribute, coefficient) => {
    const required = job[attribute].split(' ');
    if (attribute === 'complementarySkills') {
      attribute = 'skills';
    }
    const candidate = user[attribute].split(' ');
    return (stringCompare(required, candidate) > coefficient);
  }

  const expertiseFilter = (jobs) => {
    const result = jobs.filter((job) => {
      let matching = 0;
      if (checkMatching(job, 'workExperience', 0.1)) {
        matching++;
      }
      if (checkMatching(job, 'skills', 0.25)) {
        matching++;
      }
      if (checkMatching(job, 'complementarySkills', 0.5)) {
        matching++;
      }
      return matching > 1;
    });
    return result;
  }

const findJob = async () => {

  const result = await fetch('/api/vacancies/getAllTitles',
  {
    method: 'post',
    headers: {
      'Content-Type': 'application/json',
    }
  });
  let jobs = await result.json();
  jobs = typeFilter(jobs);
  jobs = salaryFilter(jobs);
  console.log(jobs);
  jobs = educationFilter(jobs);
  console.log(jobs);
  jobs = expertiseFilter(jobs);
