import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';


const SearchComponent = () => {
  const [query, setQuery] = useState('');
  const [navigate, setNavigate] = useState(false);

  const search = () => {
    setNavigate(true);
  };

  if (navigate) {
    return <Link to={`/vacancies/${query}`} />
  }





  return(
    <>
      <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css" />
      <button id="search-button" type="submit" onClick={() => search()}><i className="fa fa-search"></i></button>
      <input id="search-query" type="search" placeholder="Search.." value={query} onChange={(event) => setQuery(event.target.value)} />
    </>
  );
};

export default SearchComponent;
