import React, { useState } from 'react';
import { generate } from 'npm-password-hash';

const RegisterPage = () => {
  const [firstName, setFirstName] = useState('');
  const [surname, setSurname] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [location, setLocation] = useState('');
  const [education, setEducation] = useState('');
  const [workExperience, setWorkExperience] = useState('');
  const [skills, setSkills] = useState('');


  const register = async () => {
    if (password === confirmPassword)
    {
      const today = new Date();
      const dateAdded = today.getDate()+'.'+(today.getMonth()+1)+'.'+today.getFullYear()
                         + ' ' + today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
      const hashPassword = generate(password);
      const result = await fetch('/api/seekers/add-new', {
        method: 'post',
        body: JSON.stringify({firstName, surname, email, hashPassword, location, education,
                     workExperience, skills, dateAdded}),
        headers: {
          'Content-Type': 'application/json',
        }
      });
    } else {
      setPassword('');
      setConfirmPassword('');
    }
};


    return (
      <>
        <div>
          <h1>Register</h1>
          <label>
            First Name:
            <input type="text" value={firstName} onChange={(event) => setFirstName(event.target.value)} />
          </label>
          <label>
            Surname:
            <input type="text" value={surname} onChange={(event) => setSurname(event.target.value)} />
          </label>
          <label>
            E-mail:
            <input type="text" value={email} onChange={(event) => setEmail(event.target.value)} />
          </label>
          <label>
            Password:
            <input type="password" value={password} onChange={(event) => setPassword(event.target.value)} />
          </label>
          <label>
            Confirm Password:
            <input type="password" value={confirmPassword} onChange={(event) => setConfirmPassword(event.target.value)} />
          </label>
          <label>
            Location:
            <input type="text" value={location} onChange={(event) => setLocation(event.target.value)} />
          </label>
          <label>
            Education:
            <textarea rows="4" cols="50" value={education} onChange={(event) => setEducation(event.target.value)} />
          </label>
          <label>
            Work Experience:
            <textarea rows="4" cols="50" value={workExperience} onChange={(event) => setWorkExperience(event.target.value)} />
          </label>
          <label>
            Skills:
            <textarea rows="4" cols="50" value={skills} onChange={(event) => setSkills(event.target.value)}/>
          </label>
          <button onClick={() => register()}>Register</button>
        </div>
      </>
    );

};

export default RegisterPage;
