import React from 'react';

const JobFindingButton = ({ user }) => {

  const typeFilter = (jobs) => {
    if (user.jobType === "Both") {
      return jobs;
    } else {
      const result = jobs.filter((job) => {
        return (user.jobType === job.jobType);
      });
      return result;
    }
  }

  const salaryFilter = (jobs) => {
    const result = jobs.filter((job) => {
      return (user.salary <= job.maximumSalary);
    });
    return result;
  }

  const stringCompare = (string1, string2) => {
    let matches = 0;
    for (let i = 0; i < string1.length; i++) {
      for (let j = 0; j < string2.length; j++) {
        if (string1[i].replace(',', '').toLowerCase() === string2[j].replace(',', '').toLowerCase()) {
          matches++;
        }
      }
    }
    return matches/string1.length;
  }

  const checkMatching = (job, attribute) => {
    const required = job[attribute].split(' ');
    const candidate = user[attribute].split(' ');
    return (stringCompare(required, candidate) > 0.1);
  }

  const essentialFilter = (jobs) => {
    const result = jobs.filter((job) => {
      let matching = 0;
      if (checkMatching(job, 'education')) {
        matching++;
      }
      if (checkMatching(job, 'workExperience')) {
        matching++;
      }
      if (checkMatching(job, 'skills')) {
        matching++;
      }
      return matching > 1;
    });
    return result;
  }

const findJob = async () => {

  const result = await fetch('/api/vacancies/getAllTitles',
  {
    method: 'post',
    headers: {
      'Content-Type': 'application/json',
    }
  });
  let jobs = await result.json();
  jobs = typeFilter(jobs);
  jobs = salaryFilter(jobs);
  jobs = essentialFilter(jobs);
};


  return (
    <>
      <button onClick={findJob}>Find Job </button>
    </>
  )

}

export default JobFindingButton;
