import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const JobSearchList = () => {
  const [jobs, setJobs] = useState({jobs: []});

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch('/api/vacancies/getAllTitles',
      {
        method: 'post'
      });
      const body = await result.json();
      setJobs(body);
    }
    fetchData();
  }, []);

  const updateFilter = () => {
    const checked = document.querySelectorAll('[name="filter"]:checked');
    console.log(checked);




  };

  const jobsArray = jobs.jobs;



  return(
  <>
    <h1> Various IT jobs </h1>

    <div className="filter">
      <p> Job Types: </p>
      <input type="checkbox" name="filter" onClick={() => updateFilter()} />Permanent <br />
      <input type="checkbox" name="filter" onClick={() => updateFilter()} />Contract <br />

      <p> Salary: </p>
      <input type="checkbox" name="filter" onClick={() => updateFilter()} />300£ - 400£ per day <br />
      <input type="checkbox" name="filter" onClick={() => updateFilter()} />400£ - 500£ per day <br />
      <input type="checkbox" name="filter" onClick={() => updateFilter()} />500£ - 600£ per day <br />
      <input type="checkbox" name="filter" onClick={() => updateFilter()} />600£ - 700£ per day <br /><br />
      <input type="checkbox" name="filter" onClick={() => updateFilter()} />Under 35000£ per day <br />
      <input type="checkbox" name="filter" onClick={() => updateFilter()} />35000£ - 50000£ monthly <br />
      <input type="checkbox" name="filter" onClick={() => updateFilter()} />50000£ - 75000£ monthly <br />
      <input type="checkbox" name="filter" onClick={() => updateFilter()} />75000£ - 100000£ monthly <br />
      <input type="checkbox" name="filter" onClick={() => updateFilter()} />100000£ - 150000£ monthly <br />
      <input type="checkbox" name="filter" onClick={() => updateFilter()} />Over 150000£ monthly <br />
    </div>




    <div>
      <ul>
        {jobsArray.map((job, key) => (
          <li key={key}>
            <Link to={`/vacancies/${job.jobTitle}`}> {job.jobTitle} </Link>
            <p>{job.companyName}</p>
            <p>{job.minimumSalary}£ - {job.maximumSalary}£</p>
            <p>{job.location}</p>
            <p>{job.essentialRequirements}</p>
            <p>{job.beneficialRequirements}</p>
            <p>{job.description}</p>
            <p>{job.dateAdded}</p>
            <br></br>
          </li>
        ))}
      </ul>
    </div>
  </>
)};


export default JobSearchList;
