import React, { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import ApplyButton from '../components/ApplyButton'

const JobPage = ({ match, user }) => {
  const name = match.params.name;

  const [jobInfo, setJobInfo] = useState({ jobTitle: '' });
  const [navigate, setNavigate] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch(`/api/vacancies/${name}`);
      const body = await result.json()
      if (body === null) {
        setNavigate(true);
      } else {
        setJobInfo(body);
        console.log(body);
      }
    }
    fetchData();
  }, [name]);

  if (navigate) {
    return <Redirect to="404" />
  }




  return(
    <>
      <h1>{jobInfo.jobTitle}</h1>
      <p>Company Name: {jobInfo.companyName}</p>
      <p>Salary: {jobInfo.minimumSalary}£ - {jobInfo.maximumSalary}£</p>
      <p>Location: {jobInfo.location}</p>
      <p>Essential Requirements: {jobInfo.essentialRequirements}</p>
      <p>Beneficial Requirements: {jobInfo.beneficialRequirements}</p>
      <p>Job Description: {jobInfo.description}</p>
      <p>Date Added: {jobInfo.dateAdded}</p>
      <ApplyButton jobTitle={name} user={user} />
    </>
  );
};

export default JobPage;
