import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';


const JobSearchList = () => {
  const [jobTitles, setJobTitles] = useState({jobs: []});

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch('/api/vacancies/getAllTitles',
      {
        method: 'post'
      });
      const body = await result.json();
      setJobTitles(body);
    }
    fetchData();
  });

  const jobsArray = jobTitles.jobs;



  return(
  <>
    <h1> Various IT jobs </h1>
    <div>
      <ul>
        {jobsArray.map((job, key) => (
          <li>
            <Link key={key} to={`/vacancies/${job.jobTitle}`}> {job.jobTitle} </Link>
            <p key={key}>{job.companyName}</p>
            <p key={key}>{job.minimumSalary} - {job.maximumSalary}</p>
            <p key={key}>{job.location}</p>
            <p key={key}>{job.essentialRequirements}</p>
            <p key={key}>{job.beneficialRequirements}</p>
            <p key={key}>{job.description}</p>
            <p key={key}>{job.dateAdded}</p>
          </li>
        ))}
      </ul>
    </div>
  </>
)};


export default JobSearchList;
