import React, {useState } from 'react';
import { Redirect } from 'react-router-dom';

const EditAccountPage = ({ user, setUser }) => {
  const [account, setAccount] = useState({ firstName: user.firstName, surname: user.surname, email: user.email, location: user.location,
                                           education: user.education, workExperience: user.workExperience,
                                           skills: user.skills, salary: user.salary, jobType: user.jobType });
  const [navigate, setNavigate] = useState(false);


  const edit = async () => {
    const result = await fetch('/api/seekers/edit', {
      method: 'post',
      body: JSON.stringify(account),
      headers: {
        'Content-Type': 'application/json',
      }
    });
    setNavigate(true);
    console.log(user);
  }

  const handleChange = (event) => {
   const accountCopy = JSON.parse(JSON.stringify(account));
   accountCopy[event.target.name] = event.target.value;
   setAccount(accountCopy);
  }

  if (navigate) {
    return <Redirect to="/my-account" />;
  }



 return (
   <>
     <h1>Edit Account</h1>
     <div>
       <div>
         <label>
           First Name:
           <input type="text" name="firstName" value={account.firstName} onChange={handleChange} />
         </label>
       </div>
       <div>
         <label>
           Surname:
           <input type="text" name="surname" value={account.surname} onChange={handleChange} />
         </label>
       </div>
       <div>
         <label>
           Location:
           <input type="text" name="location" value={account.location} onChange={handleChange} />
         </label>
       </div>
       <div>
         <label>
           Education:
           <textarea rows="4" cols="50" name="education" value={account.education} onChange={handleChange} />
         </label>
       </div>
       <div>
         <label>
           Work Experience:
           <textarea rows="4" cols="50" name="workExperience" value={account.workExperience} onChange={handleChange} />
         </label>
       </div>
       <div>
         <label>
           Skills:
           <textarea rows="4" cols="50" name="skills" value={account.skills} onChange={handleChange}/>
         </label>
       </div>
       <div>
         <label>
           Minimum Expected Salary (£/year):
           <input type="number" name="salary" value={account.salary} onChange={handleChange} />
         </label>
       </div>
       <div>
         <label>
           Employment Type:
           <select name="jobType" value={account.jobType} onChange={handleChange}>
             <option value="Permanent">Permanent</option>
             <option value="Contract">Contract</option>
             <option value="Any">Any</option>
           </select>
         </label>
       </div>
     </div>
     <button onClick={edit}>Save changes</button>
   </>
 );

};

export default EditAccountPage;
