import React, { useState, useEffect } from 'react';

const JobPage = ({ match }) => {
  const name = match.params.name;

  const [jobInfo, setJobInfo] = useState({jobTitle: 'undefined', companyName: 'undefined', salary: '',
                location: '', essentialRequirements: '', beneficialRequirements: '',
                description: '', dateAdded: ''});

  useEffect(() => {
    setJobInfo({ location: 'Belsize Park' });
  });
  return(
    <>
      <h1>{jobInfo.jobTitle}</h1>
      <p>{jobInfo.companyName}</p>
      <p>{jobInfo.salary}</p>
      <p>{jobInfo.location}</p>
      <p>{jobInfo.essentialRequirements}</p>
      <p>{jobInfo.beneficialRequirements}</p>
      <p>{jobInfo.description}</p>
      <p>{jobInfo.dateAdded}</p>
    </>
  );
};

export default JobPage;
