import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';


const JobSearchList = () => {
  const [jobTitles, setJobTitles] = useState({jobs: []});

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch('/api/vacancies/getAllTitles',
      {
        method: 'post'
      }
    );
      const body = await result.json();
      setJobTitles(body);
    }
    fetchData();
  });

  const jobsArray = jobTitles.jobs;





  return(
  <>
    <h1> Various IT jobs </h1>
    <div>
      <ul>
        {jobsArray}
        <li><Link to="/vacancies/Junior%20Tester/">Junior Tester</Link></li>
        <li><Link to="/vacancies/Lead%20Data%20Scientist">Lead Data Scientist</Link></li>
        <li><Link to="/vacancies/Junior%20Software%20Engineer">Junior Software Engineer</Link></li>
      </ul>
    </div>
  </>

)};




export default JobSearchList;
