import React, { useState, useEffect } from 'react';


const SearchComponent = () => {
  const [query, setQuery] = useState('');

  useEffect(() => {
    const listener = event => {
      if ((event.code === "Enter" || event.code === "NumpadEnter") &&
         document.getElementById("search-button").isActiveElement()) {
        document.getElementById("search-button").click();
      }
    };
    document.addEventListener("keydown", listener);
    return () => {
      document.removeEventListener("keydown", listener);
    };
  }, []);

  const search = () => {
    window.location.replace(`/vacancies/${query}`);
  };





  return(
    <>
      <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css" />
      <button id="search-button" type="submit" onClick={() => search()}><i className="fa fa-search"></i></button>
      <input id="search-query" type="search" placeholder="Search.." value={query} onChange={(event) => setQuery(event.target.value)} />
    </>
  );
};

export default SearchComponent;
