import React from 'react';
import { Link } from 'react-router-dom';
import JobFindingButton from '../components/JobFindingButton'


const ProfileInfoComponent = ({ user, setUser }) => {
  return(
    <>
      <h1> My account </h1>
      <div>
        <ul>
          <li>First Name: {user.firstName}</li>
          <li>Surname: {user.surname}</li>
          <li>E-mail: {user.email}</li>
          <li>Location: {user.location}</li>
          <li>Education: {user.education}</li>
          <li>Work Experience: {user.workExperience}</li>
          <li>Skills: {user.skills}</li>
          <li>Minimum Expected Salary: {user.salary}Â£</li>
          <li>Employment Type: {user.jobType}</li>
        </ul>
      </div>
      {(parseInt(user.search) === 0) &&
      <JobFindingButton user={user} setUser />
      }
      <Link to={'/edit-account/'}>Edit Account</Link>
    </>
  );
};

export default ProfileInfoComponent;
