import React, { useState } from 'react';
import { generate } from 'password-hash';

const RegisterPage = () => {

  const [profileInfo, setProfileInfo] = useState({ firstName: '', surname: '',
    email: '', hashPassword: '', location: '', education: '',
    workExperience: '', skills: '' , dateAdded: ''})
  const [passwords, setPasswords] = useState({ password: '', confirmPassword: '' });


  const register = async () => {
    if (passwords.password === passwords.confirmPassword)
    {
      const today = new Date();
      setProfileInfo({ dateAdded: today.getDate()+'.'+(today.getMonth()+1)+'.'+today.getFullYear()
                         + ' ' + today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds() });
      setProfileInfo({ hashPassword: generate(passwords.password) });
      const result = await fetch('/api/seekers/add-new', {
        method: 'post',
        body: JSON.stringify(profileInfo),
        headers: {
          'Content-Type': 'application/json',
        }
      });
    } else {
      setProfileInfo({ password: '' });
      setProfileInfo({ confirmPassword: '' });
    }
};


    return (
      <>
        <div>
          <h1>Register</h1>
          <label>
            First Name:
            <input type="text" name="firstName" value={profileInfo.firstName} onChange={(event) => setProfileInfo({ $name: event.target.value })} />
          </label>
          <label>
            Surname:
            <input type="text" value={profileInfo.surname} onChange={(event) => setProfileInfo({ surname: event.target.value })} />
          </label>
          <label>
            E-mail:
            <input type="text" value={profileInfo.email} onChange={(event) => setProfileInfo({ email: event.target.value })} />
          </label>
          <label>
            Password:
            <input type="password" value={passwords.password} onChange={(event) => setPasswords( {password: event.target.value })} />
          </label>
          <label>
            Confirm Password:
            <input type="password" value={passwords.confirmPassword} onChange={(event) => setPasswords( {confirmPassword: event.target.value })} />
          </label>
          <label>
            Location:
            <input type="text" value={profileInfo.location} onChange={(event) => setProfileInfo({ location: event.target.value })} />
          </label>
          <label>
            Education:
            <textarea rows="4" cols="50" value={profileInfo.education} onChange={(event) => setProfileInfo({ education: event.target.value })} />
          </label>
          <label>
            Work Experience:
            <textarea rows="4" cols="50" value={profileInfo.workExperience} onChange={(event) => setProfileInfo({ workExperience: event.target.value })} />
          </label>
          <label>
            Skills:
            <textarea rows="4" cols="50" value={profileInfo.skills} onChange={(event) => setProfileInfo({ skills: event.target.value })}/>
          </label>
          <button onClick={() => register()}>Register</button>
        </div>
      </>
    );

};

export default RegisterPage;
