import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';


const ApplyButton = ({ jobTitle, user }) => {
  const [navigateLogin, setNavigateLogin] = useState(false);
  const [navigateHomepage, setNavigateHomepage] = useState(false);

  const apply = async () => {
    if (user.email) {
      const result = await fetch(`/api/submit/${user.email}/${jobTitle}`);
      setNavigateHomepage(true);
    } else {
      setNavigateLogin(true);
    }
  }

  if (navigateLogin) {
    return <Redirect to="/login/" />
  }

  if (navigateHomepage) {
    return <Redirect to="/" />
  }



  return(
    <button onClick={apply} >Apply</button>
  );
};

export default ApplyButton;
