import React, { useState, useEffect } from 'react';


const ProfileInfoComponent = async () => {
  const [profileInfo, setProfileInfo] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      const email = JSON.parse(sessionStorage.getItem('state')).account;
      const result = await fetch(`/api/seekers/${email}`);
      const body = await result.json();
      setProfileInfo(body);
    }
    fetchData();
  });

  console.log(profileInfo);
  var array = [];
  var i;
  for(i in profileInfo) {
    array.push([i, profileInfo[i]]);
  }
  return(
    <>
      <h1> My account </h1>
      <div>
        <ul>
          {array.map((pair, key) => (
            <li>{pair[0]}: {pair[1]}</li>
          ))}
          </ul>
      </div>
    </>
  );

};

export default ProfileInfoComponent;
