import React from 'react';
import { Link } from 'react-router-dom';


const JobDropDownMenu = () => (
  <>
    <div className="dropdown">
    <button className="dropbtn">Select Job Category</button>
    <div className="dropdown-content">
      <Link to={'/vacancies/'}>IT</Link>
      <Link to={'/categories/engineering'}>Engineering</Link>
      <Link to={'/categories/biology'}>Biology</Link>
    </div>
  </div>
</>
);




export default JobDropDownMenu;
