import React, { useState } from 'react';

const RegisterPage = () => {
  const [firstName, setFirstName] = useState('');
  const [surname, setSurname] = useState('');
  const [location, setLocation] = useState('');
  const [education, setEducation] = useState('');
  const [workExperience, setWorkExperience] = useState('');
  const [skills, setSkills] = useState('');


  const register = async () => {
    const today = new Date();
    const dateAdded = today.getDate()+'.'+(today.getMonth()+1)+'.'+today.getFullYear()
                       + ' ' + today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
    const result = await fetch('/api/seekers/add-new', {
      method: 'post',
      body: JSON.stringify({firstName, surname, location, education,
                   workExperience, skills, dateAdded}),
      headers: {
        'Content-Type': 'application/json',
      }
    });
  };


    return (
      <>
        <div>
          <h1>Register</h1>
          <label>
            First Name:
            <input type="text" value={jobTitle} onChange={(event) => setJobTitle(event.target.value)} />
          </label>
          <label>
            Surname:
            <input type="text" value={companyName} onChange={(event) => setCompanyName(event.target.value)} />
          </label>
          <label>
            Location:
            <input type="text" value={salary} onChange={(event) => setSalary(event.target.value)} />
          </label>
          <label>
            Education:
            <textarea rows="4" cols="50" value={essentialRequirements} onChange={(event) => setEssentialRequirements(event.target.value)} />
          </label>
          <label>
            Work Experience:
            <textarea rows="4" cols="50" value={essentialRequirements} onChange={(event) => setEssentialRequirements(event.target.value)} />
          </label>
          <label>
            Skills:
            <textarea rows="4" cols="50" value={beneficialRequirements} onChange={(event) => setBeneficialRequirements(event.target.value)}/>
          </label>
          <button onClick={() => register()}>Register</button>
        </div>
      </>
    );

};

export default RegisterPage;
