import React, { useState, useEffect } from 'react';
import { Redirect } from 'react-router-dom';
import { verify } from 'password-hash';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');


  useEffect(() => {
    const listener = event => {
      if (event.code === "Enter" || event.code === "NumpadEnter") {
        document.getElementById("button").click();
      }
    };
    document.addEventListener("keydown", listener);
    return () => {
      document.removeEventListener("keydown", listener);
    };
  }, []);


  const login = async () => {
    const result = await fetch(`/api/seekers/${email}`);
    if (result.status !== 200) {
      setState({ state: 'incorrectEmail' });
    } else {
      const accountInfo = await result.json();
      const hashPassword = accountInfo.hashPassword;
      if (verify(password, hashPassword)) {
        setState({ state: 'loginSuccessful', account: email });
        window.location.href('/vacancies/');
      } else {
        setState({ state: 'incorrectPassword' });
      }
    }
  };


  return(
    <>
      <div>
        <h1>Login</h1>
        <label>
          E-mail:
          <input type="text" value={email} onChange={(event) => setEmail(event.target.value)} />
        </label>
        <label>
          Password:
          <input type="password" value={password} onChange={(event) => setPassword(event.target.value)} />
        </label>
        <p style={{ display: (state.state==='loginSuccessful') ? 'block' : 'none'}} > Login to {state.account} successful </p>
        <p style={{ display: (state.state==='incorrectEmail') ? 'block' : 'none'}}> This account does not exist </p>
        <p style={{ display: (state.state==='incorrectPassword') ? 'block' : 'none'}}> Incorrect password </p>
        <button id="button" onClick={() => login()}>Log in</button>
      </div>
    </>
  );




}


export default LoginPage;
