import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';


const JobSearchList = () => {
  const [jobTitles, setJobTitles] = useState({jobs: []});

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch('/api/vacancies/getAllTitles',
      {
        method: 'post'
      });
      const body = await result.json();
      setJobTitles(body);
    }
    fetchData();
  });

  const jobsArray = jobTitles.jobs;



  return(
  <>
    <h1> Various IT jobs </h1>
    <div>
      <ul>
        {jobsArray.map((job, key) => (
          <Link key={key} to={`/vacancies/${job.jobTitle}`}>
            <p>{job.jobTitle}</p>
          </Link>
        ))}
      </ul>
    </div>
  </>
)};


export default JobSearchList;
