import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const JobSearchList = () => {
  const [jobs, setJobs] = useState({jobs: []});

  useEffect(() => {
    const fetchData = async () => {
      const result = await fetch('/api/vacancies/getAllTitles',
      {
        method: 'post'
      });
      const body = await result.json();
      setJobs(body);
    }
    fetchData();
  });

  const jobsArray = jobs.jobs;



  return(
  <>
    <h1> Various IT jobs </h1>

    <div className="filter">
      <p> Salary: </p>
      <input type="radio" name="" />30000
      <input type="radio" name="" />40000
      <input type="radio" name="" />50000






    </div>




    <div>
      <ul>
        {jobsArray.map((job, key) => (
          <li key={key}>
            <Link to={`/vacancies/${job.jobTitle}`}> {job.jobTitle} </Link>
            <p>{job.companyName}</p>
            <p>{job.minimumSalary} - {job.maximumSalary}</p>
            <p>{job.location}</p>
            <p>{job.essentialRequirements}</p>
            <p>{job.beneficialRequirements}</p>
            <p>{job.description}</p>
            <p>{job.dateAdded}</p>
            <br></br>
          </li>
        ))}
      </ul>
    </div>
  </>
)};


export default JobSearchList;
