import React, { useState, useEffect } from 'react';
import AccountDropDownMenu from './AccountDropDownMenu';
import LoginRegisterComponent from './LoginRegisterComponent';



const VariableNavButtons = () => {
  const [state, setState] = useState('loggedOut');
  var x;
  const [ignored, forceUpdate] = useReducer(x => x + 1, 0);


  const logOut = () => {
    sessionStorage.removeItem('state');
    forceUpdate();
  }


  useEffect(() => {
    const fetchData =  () => {
      const result = sessionStorage.getItem('state');
      (result === null) ? setState('loggedOut') : setState('loggedIn')
    }
    fetchData();
  }, [x]);

  if (state === 'loggedOut') {
    return(<LoginRegisterComponent />);
  } else {
    return(<AccountDropDownMenu logOut={logOut} />);
  }

};

export default VariableNavButtons;
